mapper.insert:
insert into 

	matching_board
	notice_board
	appraise_board

	( boardnum
	, userid
	, nickname
	, title
	, contents
	, status

		, matchingid, appointedTime
		, datanum
	) 
values(

		matching_board_seq.nextVal
		notice_board_seq.nextVal
		appraise_board_seq.nextVal

	, #{board.userid}
	, #{board.nickname}
	, #{board.title}
	, #{board.contents}
	, #{board.status}


		, #{board.matchingId}
		, to_timestamp(#{board.appointedTime}, 'YYYY-MM-DD"T"HH24:MI')
		, #{board.datanum}
	)

mapper.update:
update
matching_board notice_board appraise_board
set hitcount = hitcount + 1 where boardnum = #{boardNum}

mapper.delete:
delete from
matching_board notice_board appraise_board
where boardnum = #{boardNum}

