mapper.insert:
INSERT INTO 
	coverletter(
		userid
		, title
		, maintext
		, address
		, phone
		, email
)VALUES(
	#{userid}
	, #{title}
	, #{maintext}
	, #{address}
	, #{phone}
	, #{email})

mapper.update:
UPDATE 
	coverletter 
SET
	maintext = #{maintext}
	, address = #{address}
	, phone = #{phone}
	, email = #{email}
WHERE 
	userid = #{userid} 
and 
	title = #{title}

mapper.delete:
delete from 
	coverletter 
where 
	userid = #{userid} 
and 
	title = #{title}

mapper.select:
select count(*) from
resume coverletter
where userid = #{userid}
