mapper.insert:
insert into 

	matching_comment

	( commentnum
	, userid
	, boardnum
	, comments
	, parentcmtid
	, groupnum

		, pronunciation, contents, posture
	)
values(

		mc_commentnum_seq.nextVal

	, #{comment.userid}
	, #{comment.boardNum}
	, #{comment.comments}
	, #{comment.parentCmtId}
	, mc_groupnum_seq.nextVal
	, #{comment.groupNum}


		, #{comment.pronunciation}
		, #{comment.contents}
		, #{comment.posture}

	)

mapper.update:
update

	matching_comment

set
	comments = #{comment.comments}
	, regdate = sysdate


		, pronunciation = #{comment.pronunciation}
		, contents = #{comment.contents}
		, posture = #{comment.posture}


where
	commentnum = #{comment.commentNum}
