mapper.insert:
insert into 

	matching_comment
	notice_comment
	appraise_comment

	( commentnum
	, userid
	, nickname
	, boardnum
	, comments
	, parentid
	, parentnick
	, groupnum

		, pronunciation, contents, posture
	)
values(

		mc_commentnum_seq.nextVal
		nc_commentnum_seq.nextVal
		ac_commentnum_seq.nextVal

	, #{comment.userid}
	, #{comment.nickname}
	, #{comment.boardNum}
	, #{comment.comments}
	, #{comment.parentId}
	, #{comment.parentNick}


		, mc_groupnum_seq.nextVal
		, nc_groupnum_seq.nextVal
		, ac_groupnum_seq.nextVal


	, #{comment.groupNum}
	 , #{comment.pronunciation}
										, #{comment.contents}
										, #{comment.posture}
	)

mapper.update:
update

	matching_comment
	notice_comment
	appraise_comment

set
	comments = #{comment.comments}
	, regdate = sysdate


	, userid = #{comment.userid}
	, nickname = #{comment.nickname}

where
	commentnum = #{comment.commentNum}

mapper.delete:
delete from
matching_comment notice_comment appraise_comment
where commentnum = #{commentNum}

mapper.update:
update
	matching_comment
set
	matchingid = #{comment.matchingId}
where
	commentnum = #{comment.commentNum}
